\usepackage{mathrsfs}
\setlength{\oddsidemargin}{-69pt}
\begin{document}
}{}\begin{equation*}q\ = \frac{{{y_A}{y_B}}}{{{y_{AB}}}}\ = \frac{{\left[ {HrdB} \right]\left[ {RbpA} \right]}}{{\left[ {HrdB-RbpA} \right]}}\ \end{equation*}\end{document}

